mapper.delete:

delete from sys_role_menu
where role_id = #{roleId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}

mapper.insert:

insert into sys_role_menu (role_id, menu_id, permission_, 
  enable_, remark_, create_by, 
  create_time, update_by, update_time
  )
values (#{roleId,jdbcType=VARCHAR}, #{menuId,jdbcType=VARCHAR}, #{permission,jdbcType=VARCHAR}, 
  #{enable,jdbcType=BIT}, #{remark,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:

update sys_role_menu
set enable_ = #{enable,jdbcType=BIT},
  remark_ = #{remark,jdbcType=VARCHAR},
  create_by = #{createBy,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_by = #{updateBy,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}

mapper.select:

select role_id, menu_id, permission_, enable_, remark_, create_by, create_time, update_by, 
update_time
from sys_role_menu
where role_id = #{roleId,jdbcType=VARCHAR}
  and menu_id = #{menuId,jdbcType=VARCHAR}
  and permission_ = #{permission,jdbcType=VARCHAR}
