mapper.select:
select id_ from sys_user where user_type  <>  1
and enable_=#{enable}
and account_=#{account}
and password_=#{password}
and dept_id=#{deptid}

	and (account_ like CONCAT('%',#{keyword},'%') or user_name like CONCAT('%',#{keyword},'%') or phone_ like CONCAT('%',#{keyword},'%'))

select id_ from sys_user where 1=1
and enable_=#{enable}
and account_=#{account}
and password_=#{password}
and dept_id=#{deptid}

	and (account_ like CONCAT('%',#{keyword},'%') or user_name like CONCAT('%',#{keyword},'%') or phone_ like CONCAT('%',#{keyword},'%'))
